<?phpclass BaseModel extends CActiveRecord {		public function select($select='*'){		$this->getDbCriteria()->mergeWith(array(        'select'=>$select,    	));    	return $this;	}	public function order($order='recommend_level desc,id desc')	{    	$this->getDbCriteria()->mergeWith(array(        'order'=>$order,    	));    	return $this;	}		public function limit($limit){		$this->getDbCriteria()->mergeWith(array(        'limit'=>$limit,    	));    	return $this;	}	public function offset($offset=-1){		$this->getDbCriteria()->mergeWith(array(        'offset'=>$offset,    	));    	return $this;	}    /**     * save all table's public fields     * eg: createtime, updatetime     * @return bool     */    protected function beforeSave()    {        if(parent::beforeSave())        {            if($this->isNewRecord)            {                if (!isset($this->createtime) || null == $this->createtime)                    $this->createtime = time();                if (!isset($this->updatetime) || null == $this->updatetime)                    $this->updatetime = time();                $this->status = Yii::app()->params['status']['ischecked'];            }            else            {                if (!isset($this->updatetime) || null == $this->updatetime)                    $this->updatetime = time();            }            return true;        }        else            return false;    }    /**     * mark delete, not really remove     * @return bool     */    protected function beforeDelete()    {        $this->status = Yii::app()->params['status']['isdelete'];        $this->save();        return false;    }}